from setuptools import setup

setup(
    name="jupyter-streamlit-launcher",
    version="1.0.0",
    description="JupyterLab launcher integration for Streamlit via jupyter-server-proxy",
    packages=["streamlit_launcher"],
    package_dir={"streamlit_launcher": "."},
    package_data={"streamlit_launcher": ["icon.svg"]},
    entry_points={
        "jupyter_serverproxy_servers": [
            "streamlit = streamlit_launcher:setup_streamlit",
        ],
    },
    install_requires=[
        "jupyter-server-proxy",
        "streamlit",
    ],
)
